    LANGUAGE plpgsql
    AS $$
BEGIN
  IF NEW.status = 'failed' OR NEW.status = 'cancelled' THEN
    UPDATE workflow_actions
    SET status = 'cancelled',
        completed_at = now()
    WHERE execution_id = NEW.id
      AND status IN ('pending', 'running');

