CREATE FUNCTION public.contact_agreements_legacy_insert() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
DECLARE
  v_contract_type contract_type;
  v_contract_status contract_status;
  v_contract_id uuid;
BEGIN
  -- Map legacy agreement_type to contract_type enum
  v_contract_type := CASE NEW.agreement_type
    WHEN 'msa' THEN 'msa'
    WHEN 'nda' THEN 'nda'
    WHEN 'sow' THEN 'sow'
    WHEN 'rate_card' THEN 'rate_card_agreement'
    WHEN 'sla' THEN 'sla'
    WHEN 'vendor_agreement' THEN 'vendor_agreement'
    ELSE 'other'
  END::contract_type;

  -- Map legacy status to contract_status enum
  v_contract_status := CASE NEW.status
    WHEN 'draft' THEN 'draft'
    WHEN 'pending' THEN 'pending_signature'
    WHEN 'active' THEN 'active'
    WHEN 'expired' THEN 'expired'
    WHEN 'terminated' THEN 'terminated'
    ELSE 'draft'
  END::contract_status;

  INSERT INTO contracts (
    org_id,
    entity_type,
    entity_id,
    contract_name,
    contract_type,
    effective_date,
    expiry_date,
    auto_renew,
    renewal_notice_days,
    status,
    document_url,
    signed_document_url,
    terms,
    created_at,
    updated_at
  ) VALUES (
    NEW.org_id,
    'contact',
    NEW.contact_id,
    COALESCE(NEW.agreement_name, 'Agreement'),
    v_contract_type,
    NEW.effective_date,
    NEW.expiry_date,
    COALESCE(NEW.auto_renew, false),
    COALESCE(NEW.renewal_notice_days, 30),
    v_contract_status,
    NEW.document_url,
    NEW.signed_document_url,
    COALESCE(NEW.terms, '{}'::jsonb),
    COALESCE(NEW.created_at, now()),
    COALESCE(NEW.updated_at, now())
  )
  RETURNING id INTO v_contract_id;

  NEW.id := v_contract_id;

  -- Insert internal signer if provided
  IF NEW.our_signer_id IS NOT NULL THEN
    INSERT INTO contract_parties (
      org_id,
      contract_id,
      party_type,
      party_role,
      user_id,
      signatory_status,
      signed_at,
      signing_order,
      is_required
    ) VALUES (
      NEW.org_id,
      v_contract_id,
      'internal',
      'company',
      NEW.our_signer_id,
      CASE WHEN NEW.our_signed_at IS NOT NULL THEN 'signed' ELSE 'pending' END::signatory_status,
      NEW.our_signed_at,
      1,
      true
    );
  END IF;

  -- Insert external signer if provided
  IF NEW.their_signer_name IS NOT NULL THEN
    INSERT INTO contract_parties (
      org_id,
      contract_id,
      party_type,
      party_role,
      party_name,
      signatory_status,
      signed_at,
      signing_order,
      is_required
    ) VALUES (
      NEW.org_id,
      v_contract_id,
      'individual',
      'client',
      NEW.their_signer_name,
      CASE WHEN NEW.their_signed_at IS NOT NULL THEN 'signed' ELSE 'pending' END::signatory_status,
      NEW.their_signed_at,
      2,
      true
    );
  END IF;

  RETURN NEW;
END;
$$;


