-- Name: find_or_create_communication_thread(uuid, uuid, text, character varying); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.find_or_create_communication_thread(p_org_id uuid, p_contact_id uuid, p_subject text, p_channel character varying DEFAULT 'email'::character varying) RETURNS uuid
    LANGUAGE plpgsql
    AS $$
DECLARE
  v_thread_id UUID;
  v_clean_subject TEXT;
BEGIN
  -- Clean subject (remove Re:, Fwd:, etc.)
  v_clean_subject := regexp_replace(p_subject, '^(Re:|Fwd:|FW:|RE:)\s*', '', 'gi');
  v_clean_subject := trim(v_clean_subject);

  -- Find existing thread by subject and contact
  SELECT thread_id INTO v_thread_id
  FROM communications
  WHERE org_id = p_org_id
    AND contact_id = p_contact_id
    AND channel = p_channel
    AND (
      subject ILIKE v_clean_subject
      OR subject ILIKE 'Re: ' || v_clean_subject
      OR subject ILIKE 'Fwd: ' || v_clean_subject
    )
    AND created_at > now() - INTERVAL '30 days'
  ORDER BY created_at DESC
  LIMIT 1;

  -- If no thread found, return NULL (will be set to the new message's ID)
  RETURN v_thread_id;
END;
$$;


--
-- Name: generate_activity_number(); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.generate_activity_number() RETURNS trigger
    LANGUAGE plpgsql
    AS $_$
DECLARE
  v_prefix TEXT;
  v_year TEXT;
  v_seq INTEGER;
  v_activity_number TEXT;
BEGIN
  -- Only generate if not already set
  IF NEW.activity_number IS NOT NULL THEN
    RETURN NEW;
  END IF;
  
  -- Get prefix based on activity type
  v_prefix := CASE NEW.activity_type
    WHEN 'call' THEN 'CALL'
    WHEN 'email' THEN 'EMAIL'
    WHEN 'meeting' THEN 'MTG'
    WHEN 'task' THEN 'TASK'
    WHEN 'note' THEN 'NOTE'
    WHEN 'interview' THEN 'INT'
    WHEN 'follow_up' THEN 'FU'
    ELSE 'ACT'
  END;
  
  v_year := TO_CHAR(NOW(), 'YYYY');
  
  -- Get next sequence number for this org + year
  SELECT COALESCE(MAX(
    CASE 
      WHEN activity_number ~ ('^' || v_prefix || '-' || v_year || '-[0-9]+$')
      THEN CAST(SUBSTRING(activity_number FROM '[0-9]+$') AS INTEGER)
      ELSE 0
    END
  ), 0) + 1 INTO v_seq
  FROM activities
  WHERE org_id = NEW.org_id
    AND activity_number LIKE v_prefix || '-' || v_year || '-%';
  
  -- Generate activity number: PREFIX-YYYY-NNNNN
  v_activity_number := v_prefix || '-' || v_year || '-' || LPAD(v_seq::TEXT, 5, '0');
  
  NEW.activity_number := v_activity_number;
  RETURN NEW;
END;
$_$;


