    COUNT(*) FILTER (WHERE cp.converted_to_lead_at IS NOT NULL)::BIGINT AS leads
  FROM campaign_prospects cp
  WHERE cp.campaign_id = p_campaign_id
  GROUP BY cp.primary_channel;
