    LANGUAGE plpgsql
    AS $$
BEGIN
  RETURN QUERY
  SELECT
    cs.id,
    cs.user_id,
    up.full_name as student_name,
    up.email as student_email,
    cs.enrollment_id,
    cs.course_id,
    c.title as course_title,
    cs.repository_url,
    cs.demo_video_url,
