    LANGUAGE plpgsql SECURITY DEFINER
    AS $$
    BEGIN
      RETURN QUERY
      SELECT
        mt.id AS topic_id,
        mt.title AS topic_title,
        COUNT(DISTINCT rp.user_id)::INTEGER AS total_readers,
