    LANGUAGE plpgsql SECURITY DEFINER
    AS $$
BEGIN
  RETURN QUERY
  SELECT
    lg.rank,
    lg.total_xp,
    ROUND((1 - (lg.rank::numeric / total_count.count::numeric)) * 100, 1) as percentile
