    LANGUAGE plpgsql
    AS $$
DECLARE
  v_audit_id UUID;
  v_user_email TEXT;
  v_changed_fields TEXT[];
BEGIN
  -- Get user email for denormalization
  SELECT email INTO v_user_email
  FROM user_profiles
  WHERE id = p_user_id;

  -- Calculate changed fields (for UPDATE actions)
  IF p_action = 'UPDATE' AND p_old_values IS NOT NULL AND p_new_values IS NOT NULL THEN
    SELECT ARRAY_AGG(key)
    INTO v_changed_fields
    FROM jsonb_each(p_new_values)
    WHERE value != COALESCE(p_old_values->key, 'null'::jsonb);
  END IF;

  -- Insert audit log
  INSERT INTO audit_logs (
    table_name,
    action,
    record_id,
    user_id,
    user_email,
    old_values,
    new_values,
    changed_fields,
    metadata,
    severity
  ) VALUES (
    p_table_name,
    p_action,
    p_record_id,
    p_user_id,
    v_user_email,
    p_old_values,
    p_new_values,
    v_changed_fields,
    p_metadata,
    p_severity
  ) RETURNING id INTO v_audit_id;

  RETURN v_audit_id;
END;
$$;


