    LANGUAGE plpgsql SECURITY DEFINER
    AS $$
DECLARE
  v_initial_state_id UUID;
  v_instance_id UUID;
BEGIN
  -- Get initial state
  SELECT id INTO v_initial_state_id
  FROM workflow_states
  WHERE workflow_id = p_workflow_id
    AND is_initial = TRUE
  LIMIT 1;

  IF v_initial_state_id IS NULL THEN
    RAISE EXCEPTION 'Workflow % has no initial state', p_workflow_id;
  END IF;

  -- Create instance
  INSERT INTO workflow_instances (
    org_id,
    workflow_id,
    entity_type,
    entity_id,
    current_state_id,
    created_by
  ) VALUES (
    p_org_id,
    p_workflow_id,
    p_entity_type,
    p_entity_id,
    v_initial_state_id,
    p_user_id
  ) RETURNING id INTO v_instance_id;

  -- Record initial state in history
  INSERT INTO workflow_history (
    workflow_instance_id,
    from_state_id,
    to_state_id,
    action,
    performed_by,
    notes
  ) VALUES (
    v_instance_id,
    NULL,
    v_initial_state_id,
    'start',
    p_user_id,
    'Workflow started'
  );

  -- Publish event
  PERFORM publish_event(
    'workflow.started',
    p_entity_id,
    jsonb_build_object(
      'instanceId', v_instance_id,
      'workflowId', p_workflow_id,
      'entityType', p_entity_type,
      'entityId', p_entity_id,
      'initialState', v_initial_state_id
    ),
    p_user_id,
    '{}'::jsonb
  );

  RETURN v_instance_id;
END;
$$;


