--
-- Name: contact_agreements_legacy; Type: VIEW; Schema: public; Owner: -
--

CREATE VIEW public.contact_agreements_legacy AS
 SELECT id,
    org_id,
    entity_id AS contact_id,
        CASE (contract_type)::text
            WHEN 'msa'::text THEN 'msa'::text
            WHEN 'nda'::text THEN 'nda'::text
            WHEN 'sow'::text THEN 'sow'::text
            WHEN 'rate_card_agreement'::text THEN 'rate_card'::text
            WHEN 'sla'::text THEN 'sla'::text
            WHEN 'vendor_agreement'::text THEN 'vendor_agreement'::text
            ELSE 'other'::text
        END AS agreement_type,
    contract_name AS agreement_name,
    effective_date,
    expiry_date,
    auto_renew,
    renewal_notice_days,
        CASE (status)::text
            WHEN 'draft'::text THEN 'draft'::text
            WHEN 'pending_review'::text THEN 'pending'::text
            WHEN 'pending_signature'::text THEN 'pending'::text
            WHEN 'partially_signed'::text THEN 'pending'::text
            WHEN 'active'::text THEN 'active'::text
            WHEN 'expired'::text THEN 'expired'::text
            WHEN 'terminated'::text THEN 'terminated'::text
            WHEN 'renewed'::text THEN 'active'::text
            WHEN 'superseded'::text THEN 'expired'::text
            ELSE 'draft'::text
        END AS status,
    document_url,
    signed_document_url,
    ( SELECT cp.user_id
           FROM public.contract_parties cp
          WHERE ((cp.contract_id = c.id) AND ((cp.party_type)::text = 'internal'::text) AND (cp.is_required = true))
         LIMIT 1) AS our_signer_id,
    ( SELECT cp.signed_at
           FROM public.contract_parties cp
          WHERE ((cp.contract_id = c.id) AND ((cp.party_type)::text = 'internal'::text) AND (cp.is_required = true) AND (cp.signatory_status = 'signed'::public.signatory_status))
         LIMIT 1) AS our_signed_at,
    ( SELECT COALESCE(cp.party_name, (concat_ws(' '::text, ct.first_name, ct.last_name))::character varying) AS "coalesce"
           FROM (public.contract_parties cp
             LEFT JOIN public.contacts ct ON ((ct.id = cp.contact_id)))
          WHERE ((cp.contract_id = c.id) AND ((cp.party_type)::text <> 'internal'::text) AND (cp.is_required = true))
         LIMIT 1) AS their_signer_name,
    ( SELECT cp.signed_at
           FROM public.contract_parties cp
          WHERE ((cp.contract_id = c.id) AND ((cp.party_type)::text <> 'internal'::text) AND (cp.is_required = true) AND (cp.signatory_status = 'signed'::public.signatory_status))
         LIMIT 1) AS their_signed_at,
    terms,
    created_at,
    updated_at,
    deleted_at
   FROM public.contracts c
  WHERE ((entity_type)::text = 'contact'::text);


