CREATE VIEW public.contact_compliance_legacy AS
 SELECT ci.id,
    ci.org_id,
    ci.entity_id AS contact_id,
    COALESCE(ci.compliance_type, cr.category) AS compliance_type,
        CASE (ci.status)::text
            WHEN 'pending'::text THEN 'pending'::text
            WHEN 'received'::text THEN 'received'::text
            WHEN 'under_review'::text THEN 'received'::text
            WHEN 'verified'::text THEN 'verified'::text
            WHEN 'expiring'::text THEN 'expiring'::text
            WHEN 'expired'::text THEN 'expired'::text
            WHEN 'rejected'::text THEN 'rejected'::text
            WHEN 'waived'::text THEN 'verified'::text
            ELSE 'pending'::text
        END AS status,
    ci.document_url,
    ci.document_received_at,
    ci.effective_date,
    ci.expiry_date,
    ci.verified_by,
    ci.verified_at,
    ci.verification_notes,
    ci.policy_number,
    ci.coverage_amount,
    ci.insurance_carrier,
    ci.expiry_alert_sent_at,
    ci.created_at,
    ci.updated_at,
    ci.deleted_at
   FROM (public.compliance_items ci
     LEFT JOIN public.compliance_requirements cr ON ((cr.id = ci.requirement_id)))
  WHERE ((ci.entity_type)::text = 'contact'::text);


