CREATE VIEW public.organization_addresses_view AS
 SELECT o.id AS organization_id,
    o.name AS organization_name,
    addr.id AS address_id,
    addr.address_type,
    addr.address_line_1,
    addr.address_line_2,
    addr.address_line_3,
    addr.city,
    addr.state_province,
    addr.postal_code,
    addr.country_code,
    addr.county,
    addr.latitude,
    addr.longitude,
    addr.is_verified,
    addr.is_primary,
    addr.effective_from,
    addr.effective_to,
    addr.notes,
    addr.created_at,
    addr.updated_at
   FROM (public.organizations o
     LEFT JOIN public.addresses addr ON (((addr.entity_type = 'organization'::text) AND (addr.entity_id = o.id))));


