package acme.ta.webservice.contact

uses gw.xml.ws.annotation.WsiWebService
uses gw.xml.ws.annotation.WsiPermissions
uses gw.xml.ws.annotation.WsiAvailability

@WsiWebService
@WsiPermissions({"soapadmin"})
@WsiAvailability(MAINTENANCE)
class ContactAPI {
    function doesContactExist(publicId : String) : boolean {
        var queryObj = gw.api.database.Query.make(ABContact)
        queryObj.compare("PublicID", Equals, publicId)
        return not queryObj.select().Empty
    }

    function getContactInfo(publicId : String) : ContactInfo {
        // ... implementation to retrieve contact and map to ContactInfo ...
        return new ContactInfo()
    }

    function setContactEmail(publicId : String, email : String) : void {
        gw.transaction.Transaction.runWithNewBundle(\ newBundle -> {
            var queryObj = gw.api.database.Query.make(ABContact)
            queryObj.compare("PublicID", Equals, publicId)
            var targetContact = queryObj.select().AtMostOneRow
            if (targetContact != null) {
                newBundle.add(targetContact)
                targetContact.EmailAddress1 = email
            }
        })
    }
}

package acme.ta.webservice.contact

uses gw.xml.ws.annotation.WsiExportable

@WsiExportable
final class ContactInfo {
    var _contactName : String as ContactName
    var _contactType : String as ContactType
    var _primaryPhone : String as PrimaryPhone
    var _email : String as Email
}

